-- CREATE TABLE robins (
--   id INTEGER PRIMARY KEY AUTOINCREMENT,
--   full_name TEXT,
--   alias TEXT,
--   bio TEXT,
--   cause_of_death TEXT
-- );
--

INSERT INTO robins (full_name, alias, bio, cause_of_death, image)
  VALUES ("Dick Grayson", "Nightwing, Batman", "Richard John Dick Grayson was once Batman's first sidekick Robin, before becoming Nightwing and he later once took on the mantle of Batman when Bruce was thought to be dead, but once again became Nightwing after the events of Flashpoint.", "NA", "http://vignette4.wikia.nocookie.net/batman/images/0/0e/Batman_family_robin_DG.jpg/revision/latest?cb=20090613224743");
INSERT INTO robins (full_name, alias, bio, cause_of_death, image)
  VALUES ("Jason Todd", "The Red Hood, Red Robin", "While Jason soon proved to be one of Batman's most enthusiastic students, he was also the most troubled. Brash and impulsive, Jason's former life on the streets had left him with an ambiguous sense of right and wrong. This often placed Jason in opposition to the values his mentor was trying to teach him. Jason often uses excessive force to subdue criminals.", "Beaten to Death by the Joker, as voted by fans", "http://vignette3.wikia.nocookie.net/batman/images/0/0a/1573676-111585_jason_todd_400.jpg/revision/latest?cb=20120603110706");
INSERT INTO robins (full_name, alias, bio, cause_of_death, image)
  VALUES ("Tim Drake", "Red Robin", "Tim distinguished himself from Jason Todd by being far more reserved in temperament and from Dick Grayson by being more of a detective and less of a great acrobat.", "NA", "http://vignette1.wikia.nocookie.net/batman/images/a/ad/200px-Robin2.jpg/revision/latest?cb=20080601164557");
INSERT INTO robins (full_name, alias, bio, cause_of_death, image)
  VALUES ("Stephanie Brown", "Batgirl, Spoiler", "Creating a homemade Robin costume, Stephanie snuck into the Batcave and demanded that Batman train her as the new Robin.", "Tortured by the Black Mask, died in hospital soon after", "http://vignette2.wikia.nocookie.net/batman/images/b/b9/Appearancestephasrobin.jpg/revision/latest?cb=20111104204818");
INSERT INTO robins (full_name, alias, bio, cause_of_death, image)
  VALUES ("Damian Wayne", "NA", "Damian is the child of Bruce Wayne and Talia al Ghul and thus the grandson of Batman's villain, Ra's al Ghul.", "Impaled by the Heretic", "http://vignette4.wikia.nocookie.net/batman/images/b/bf/1546249-superman_batman_077001.jpg/revision/latest?cb=20110829181631");
